import 'dart:convert';
import 'dart:io';

import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:flutter_dotenv/flutter_dotenv.dart';
import 'package:flutter_secure_storage/flutter_secure_storage.dart';
import 'package:http/http.dart' as http;

import '../constants.dart';

class VisitorsScreen extends StatefulWidget {
  VisitorsScreen({Key key}) : super(key: key);

  @override
  State<StatefulWidget> createState() => _VisitorsScreeState();
}

class _VisitorsScreeState extends State<VisitorsScreen> {
  final storage = FlutterSecureStorage();

  Future<List<Visitor>> visitors;

  @override
  void initState() {
    super.initState();
    visitors = _getVisitors();
  }

  @override
  Widget build(BuildContext context) => Scaffold(
      drawer: Drawer(),
      appBar: AppBar(
        title: Text('Visiteurs'),
      ),
      body: Padding(
          padding: EdgeInsets.all(16),
          child: FutureBuilder<List<Visitor>>(
              future: visitors,
              builder: (context, snapshot) {
                if (snapshot.hasData) {
                  return ListView.builder(
                      itemCount: snapshot.data.length,
                      itemBuilder: (context, index) {
                        return Card(
                          elevation: 2,
                          child: Container(
                            decoration: BoxDecoration(color: Colors.white),
                            child: ListTile(
                              title: Text(
                                  "${snapshot.data[index].firstName} ${snapshot.data[index].lastName}"),
                            ),
                          ),
                        );
                      });
                } else {
                  return Center(child: CircularProgressIndicator());
                }
              })),
      floatingActionButton: FloatingActionButton(
        onPressed: () {
          //_addVisitor(Visitor('18436310', 'Michel', 'Parpaillon'),
          //    'data:image/png;base64,');
        },
        child: Icon(Icons.camera_alt),
      ));

  Future<List<Visitor>> _getVisitors() async {
    final tenant = await storage.read(key: STORAGE_KEY_TENANT);
    final accessToken = await storage.read(key: STORAGE_KEY_ACCESS_TOKEN);
    final event = await storage.read(key: STORAGE_KEY_EVENT);

    final response = await http.get(
        '${DotEnv().env[ENV_KEY_API_URL]}/$tenant/events/$event/visitors',
        headers: {HttpHeaders.authorizationHeader: "Bearer $accessToken"});

    if (response.statusCode == 200) {
      final _rawVisitors = jsonDecode(response.body);
      final _events = List<Visitor>();
      for (var rawEvent in _rawVisitors) {
        _events.add(Visitor.fromJson(rawEvent));
      }
      return _events;
    } else {
      throw Exception('Failed to load visitors');
    }
  }

  _addVisitor(Visitor visitor, signature) async {
    final tenant = await storage.read(key: STORAGE_KEY_TENANT);
    final accessToken = await storage.read(key: STORAGE_KEY_ACCESS_TOKEN);
    final event = await storage.read(key: STORAGE_KEY_EVENT);

    final response = await http.put(
        '${DotEnv().env[ENV_KEY_API_URL]}/$tenant/events/$event/visitors/${visitor.id}',
        headers: {
          HttpHeaders.authorizationHeader: "Bearer $accessToken"
        },
        body: {
          'firstName': visitor.firstName,
          'lastName': visitor.lastName,
          'signature': signature,
        });

    if (response.statusCode >= 200) {
      _getVisitors();
    } else {
      throw Exception('Cannot add visitor');
    }
  }
}

class Visitor {
  final String id;
  final String firstName;
  final String lastName;

  Visitor(this.id, this.firstName, this.lastName);

  Visitor.fromJson(Map<String, dynamic> json)
      : id = json['attendee_id'],
        firstName = json['firstName'],
        lastName = json['lastName'];

  Map<String, dynamic> toJson() => {
        'attendee_id': id,
        'firstName': firstName,
        'lastName': lastName,
      };
}
